import React, { useState } from 'react';
import auth from '../utils/auth';
import apiSvc from '../utils/apiSvc';
import CustomButton from './custom-button.component.jsx';

const initialState = { email: '', password: '' };

const Login = ({ setIsAuthenticated, history }) => {
  const [state, setState] = useState(initialState);

  const handleChange = (evt) => {
    const { name, value } = evt.target;
    setState((curState) => ({ ...curState, [name]: value }));
  };

  const handleSubmit = async (evt) => {
    evt.preventDefault();
    const { email, password } = state;
    const user = { email, password };
    const res = await apiSvc.login(user);
    console.log('REST TEST ', res);

    if (res.error) {
      alert(`${res.message}`);
      setState(initialState);
    } else {
      const { token } = res;
      window.localStorage.setItem('accessToken', token);
      setIsAuthenticated(true);
      auth.login(() => history.push('/profile'));
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="email"
        placeholder="Your email"
        value={state.email}
        onChange={handleChange}
      />
      <input
        type="password"
        name="password"
        placeholder="Your password"
        value={state.password}
        onChange={handleChange}
      />
      <CustomButton>SIGN ME IN</CustomButton>
    </form>
  );
};

export default Login;
