import React, { useState } from 'react';
// import auth from '../utils/auth';
// import axiosSvc from '../utils/axiosSvc';
import axios from 'axios';
import CustomButton from './custom-button.component';

const initialState = {
  address: '',
  selectedPhoto: null,
};

const ListProperty = (props) => {
  const [propertyInfo, setPropertyInfo] = useState(initialState);
  const { address, selectedPhoto } = propertyInfo;
  const formData = new FormData();
  const handleAddressChange = (evt) => {
    setPropertyInfo({ address: evt.target.value });
  };

  const handlePhotoSelect = (evt) => {
    setPropertyInfo({ selectedPhoto: evt.target.files[0] });
  };

  const handlePhotoUpload = (evt) => {
    evt.preventDefault();
    formData.append('upload', selectedPhoto);
    console.log(formData + 'fD@UPLOAD');
  };

  const handleSubmit = async (evt) => {
    evt.preventDefault();
    formData.append('address', address);
    console.log(formData, 'fD@SUBMIT');
    try {
      axios.post('http://localhost:3000/listproperty', {
        headers: { 'content-type': 'multipart/form-data' },
        body: formData,
      });
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <div onSubmit={handleSubmit}>
      <h2>Property Listing Form</h2>
      <span>Provide property address and Photo</span>
      <form>
        <input
          type="text"
          value={address}
          onChange={handleAddressChange}
          name={address}
          placeholder="Enter address"
        />
        <br />
        <input type="file" onChange={handlePhotoSelect} />
        <button onClick={handlePhotoUpload}>Upload</button>
        <CustomButton>List My Property!</CustomButton>
      </form>
    </div>
  );
};

export default ListProperty;
